import React,{useEffect} from 'react';
import ScreenHeading from '../../utilities/ScreenHeading/ScreenHeading'
import ScrollService from '../../utilities/scrollService';
import Animations from "../../utilities/Animations"

export default function AboutMe(props) {
  let fadeInScreenHandler = (screen)=>{
    if(screen.fadeScreen !== props.id)
    return;
    Animations.animations.fadeInScreen(props.id)
  }
const fadeInSubscription = ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler)

const SCREEN_CONSTANTS = {
  description: "Full stack web and mobile developer with background knowledge of MERN stacks with blah blah blah blah",
  highlights:{
    bullets:[
      "bullet point one",
      "bullet point one",
      "bullet point one",
      "bullet point one",
      "bullet point one",
      "bullet point one",
      "bullet point one",
      "bullet point one",
      "bullet point one",
      "bullet point one",
    ],
    heading:"Here is how I keep it Gangster"
  }
}
const renderHilights = ()=>{
  return(
    SCREEN_CONSTANTS.highlights.bullets.map((value, i)=>(
      <div className='highlight' key={i}><div className='highlight-blob'><span>{value}</span></div></div>
    ))
  )

}

  return (
    <div className='about-me-container screen-container' id={props.id || ""}>
      <div className='about-me-parent'>
        <ScreenHeading title={'About Me'} subHeading={'Why Choose Me?'}/>
      </div>
    </div>
  );
}
